import { useState } from "react";
import tareasPorGrupoInicial from "../data/tareas.json";
import TaskCard from "../components/TaskCard";
import EditTaskModal from "../components/EditTaskModal";
import NewGroupModal from "../components/NewGroupModal";
import NewTaskModal from "../components/NewTaskModal";

export default function Tasks() {
  const [tareasPorGrupo, setTareasPorGrupo] = useState(tareasPorGrupoInicial);
  const grupos = Object.keys(tareasPorGrupo);
  const [grupoActivo, setGrupoActivo] = useState(grupos[0]);
  const [editingTask, setEditingTask] = useState(null);
  const [showNewGroup, setShowNewGroup] = useState(false);
  const [showNewTask, setShowNewTask] = useState(false);

  const handleGrupoChange = (nuevoGrupo) => {
    setGrupoActivo(nuevoGrupo);
  };

  const handleStatusChange = (task, newStatus) => {
    const updated = tareasPorGrupo[grupoActivo].map((t) =>
      t.title === task.title && t.owner === task.owner ? { ...t, status: newStatus } : t
    );
    setTareasPorGrupo({ ...tareasPorGrupo, [grupoActivo]: updated });
  };

  const handleEditSave = (updatedTask) => {
    const updated = tareasPorGrupo[grupoActivo].map((t) =>
      t.title === updatedTask.title && t.owner === updatedTask.owner ? updatedTask : t
    );
    setTareasPorGrupo({ ...tareasPorGrupo, [grupoActivo]: updated });
  };

  const handleCreateNewGroup = (newGroupName) => {
    if (!tareasPorGrupo[newGroupName]) {
      setTareasPorGrupo({ ...tareasPorGrupo, [newGroupName]: [] });
      setGrupoActivo(newGroupName);
    }
  };

  const handleAddNewTask = (newTask) => {
    const updatedGroup = [...tareasPorGrupo[grupoActivo], newTask];
    setTareasPorGrupo({ ...tareasPorGrupo, [grupoActivo]: updatedGroup });
  };

  return (
    <div className="p-6 flex-1">
      <div className="flex justify-between items-center mb-4">
        <div>
          <h1 className="text-2xl font-bold text-darkGray mb-2">Tareas de {grupoActivo}</h1>
          <select
            value={grupoActivo}
            onChange={(e) => handleGrupoChange(e.target.value)}
            className="p-2 border rounded"
          >
            {grupos.map((grupo) => (
              <option key={grupo} value={grupo}>{grupo}</option>
            ))}
          </select>
        </div>
        <div className="flex gap-2">
          <button
            onClick={() => setShowNewGroup(true)}
            className="bg-primary text-white px-4 py-2 rounded hover:bg-blue-700"
          >
            + Nuevo Grupo
          </button>
          <button
            onClick={() => setShowNewTask(true)}
            className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
          >
            + Nueva Tarea
          </button>
        </div>
      </div>

      {tareasPorGrupo[grupoActivo].length === 0 ? (
        <p className="text-gray-500 italic">Este grupo no tiene tareas a√∫n.</p>
      ) : (
        tareasPorGrupo[grupoActivo].map((task, idx) => (
          <TaskCard
            key={idx}
            task={task}
            onStatusChange={handleStatusChange}
            onEdit={() => setEditingTask(task)}
          />
        ))
      )}

      {editingTask && (
        <EditTaskModal
          task={editingTask}
          onClose={() => setEditingTask(null)}
          onSave={handleEditSave}
        />
      )}

      {showNewGroup && (
        <NewGroupModal
          onClose={() => setShowNewGroup(false)}
          onCreate={handleCreateNewGroup}
        />
      )}

      {showNewTask && (
        <NewTaskModal
          onClose={() => setShowNewTask(false)}
          onAdd={handleAddNewTask}
        />
      )}
    </div>
  );
}
