import { Link, useLocation } from 'react-router-dom';
import { LayoutDashboard, Home, ListChecks } from 'lucide-react';

export default function Sidebar() {
  const location = useLocation();

  const menuItems = [
    { name: "Inicio", path: "/", icon: <Home size={18} /> },
    { name: "Tareas", path: "/tareas", icon: <ListChecks size={18} /> },
    { name: "Dashboard", path: "/dashboard", icon: <LayoutDashboard size={18} /> }
  ];

  return (
    <aside className="w-64 bg-white border-r h-screen p-6 shadow-md flex flex-col">
      <h2 className="text-xl font-bold text-primary mb-6 tracking-wide">Men√∫</h2>
      <nav className="space-y-2">
        {menuItems.map((item) => {
          const isActive = location.pathname === item.path;
          return (
            <Link
              key={item.name}
              to={item.path}
              className={`flex items-center gap-3 px-4 py-2 rounded-lg transition-all duration-200 ${
                isActive
                  ? 'bg-primary text-white'
                  : 'text-gray-700 hover:bg-primary/10 hover:text-primary'
              }`}
            >
              {item.icon}
              <span className="font-medium">{item.name}</span>
            </Link>
          );
        })}
      </nav>
    </aside>
  );
}
